package com;/** * Created by Oops on 11/19/2016. */import java.io.File;import javax.xml.bind.JAXBContext;import javax.xml.bind.JAXBException;import javax.xml.bind.Marshaller;public class JAXBExample {    public static void main(String[] args) {        Customer customer = new Customer();        customer.setId(1100);        customer.setName("TajBist");        customer.setAge(25);        try {            File file = new File("D:\\file1.xml");            JAXBContext jaxbContext=JAXBContext.newInstance(Customer.class);            Marshaller marshaller=jaxbContext.createMarshaller();            marshaller.setProperty(Marshaller.JAXB_FORMATTED_OUTPUT,true);            marshaller.marshal(customer,file);            marshaller.marshal(customer,System.out);        } catch (JAXBException e) {            e.printStackTrace();        }    }}